import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CartService } from '../../services/cart.service';
import { Car } from '../../models/car.model';

interface CartItem {
  car: Car;
  quantity: number;
}

@Component({
  selector: 'app-cart-view',
  template: `
    <div class="cart-container">
      <h2>Shopping Cart</h2>
      <div class="cart-items">
        <div *ngFor="let item of cartItems$ | async" class="cart-item">
          <div class="item-image">
            <img [src]="item.car.imageUrl" 
                 [alt]="item.car.brand + ' ' + item.car.model"
                 (error)="onImageError($event)">
          </div>
          <div class="item-details">
            <h3>{{item.car.brand}} {{item.car.model}}</h3>
            <p class="price">{{item.car.price | currency:'USD'}}</p>
            <div class="quantity-controls">
              <button (click)="updateQuantity(item, -1)">-</button>
              <span>{{item.quantity}}</span>
              <button (click)="updateQuantity(item, 1)">+</button>
            </div>
            <button class="remove-btn" (click)="removeItem(item)">Remove</button>
          </div>
        </div>
      </div>
      <div class="cart-summary" *ngIf="cartTotal$ | async as total">
        <p class="subtotal">Subtotal: {{total | currency:'USD'}}</p>
        <button class="checkout-btn" (click)="checkout()">Proceed to Checkout</button>
        <button class="continue-btn" (click)="continueShopping()">Continue Shopping</button>
      </div>
    </div>
  `,
  styles: [`
    .cart-container {
      padding: 2rem;
      max-width: 1200px;
      margin: 0 auto;
    }

    .cart-items {
      margin: 2rem 0;
    }

    .cart-item {
      display: flex;
      gap: 2rem;
      padding: 1rem;
      border-bottom: 1px solid #eee;
      align-items: center;
    }

    .item-image {
      width: 100px;
      height: 100px;
      flex-shrink: 0;

      img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        border-radius: 4px;
      }
    }

    .item-details {
      flex-grow: 1;

      h3 {
        margin: 0 0 0.5rem;
      }

      .price {
        color: #007bff;
        font-weight: bold;
        font-size: 1.2rem;
      }
    }

    .quantity-controls {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin: 1rem 0;

      button {
        padding: 0.25rem 0.75rem;
        border: 1px solid #ddd;
        background: white;
        cursor: pointer;
        border-radius: 4px;

        &:hover {
          background: #f5f5f5;
        }
      }
    }

    .remove-btn {
      padding: 0.5rem 1rem;
      background: #dc3545;
      color: white;
      border: none;
      border-radius: 4px;
      cursor: pointer;

      &:hover {
        background: #c82333;
      }
    }

    .cart-summary {
      margin-top: 2rem;
      padding: 1rem;
      background: #f8f9fa;
      border-radius: 4px;

      .subtotal {
        font-size: 1.2rem;
        font-weight: bold;
        margin-bottom: 1rem;
      }

      button {
        width: 100%;
        padding: 0.75rem;
        margin: 0.5rem 0;
        border: none;
        border-radius: 4px;
        cursor: pointer;
        font-size: 1rem;
      }

      .checkout-btn {
        background: #28a745;
        color: white;

        &:hover {
          background: #218838;
        }
      }

      .continue-btn {
        background: #6c757d;
        color: white;

        &:hover {
          background: #5a6268;
        }
      }
    }
  `]
})
export class CartViewComponent implements OnInit {
  cartItems$ = this.cartService.cartItems$;
  cartTotal$ = this.cartService.cartTotal$;

  constructor(
    private router: Router,
    private cartService: CartService
  ) {}

  ngOnInit(): void {}

  updateQuantity(item: CartItem, change: number): void {
    const newQuantity = item.quantity + change;
    if (newQuantity > 0) {
      this.cartService.updateQuantity(item.car.id, newQuantity);
    }
  }

  removeItem(item: CartItem): void {
    this.cartService.removeFromCart(item.car.id);
  }

  checkout(): void {
    this.router.navigate(['/checkout']);
  }

  continueShopping(): void {
    this.router.navigate(['/cars']);
  }

  onImageError(event: Event): void {
    const img = event.target as HTMLImageElement;
    if (img) {
      img.src = 'assets/cars/placeholder.jpg';
    }
  }
}
