import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { map } from 'rxjs/operators';
import { Car } from '../models/car.model';

export interface CartItem {
  car: Car;
  quantity: number;
}

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private readonly CART_STORAGE_KEY = 'car_ecommerce_cart';
  private cartItemsSubject = new BehaviorSubject<CartItem[]>([]);
  cartItems$ = this.cartItemsSubject.asObservable();

  cartCount$ = this.cartItems$.pipe(
    map(items => items.reduce((total, item) => total + item.quantity, 0))
  );

  cartTotal$ = this.cartItems$.pipe(
    map(items => items.reduce((total, item) => total + (item.car.price * item.quantity), 0))
  );

  constructor() {
    this.loadCartFromStorage();
  }

  loadCartFromStorage(): void {
    try {
      const savedCart = localStorage.getItem(this.CART_STORAGE_KEY);
      if (savedCart) {
        const parsedCart = JSON.parse(savedCart);
        this.cartItemsSubject.next(parsedCart);
      } else {
        this.cartItemsSubject.next([]);
      }
    } catch (error) {
      console.error('Error loading cart from storage:', error);
      this.cartItemsSubject.next([]);
      localStorage.removeItem(this.CART_STORAGE_KEY);
    }
  }

  addToCart(car: Car, quantity: number = 1): void {
    const currentItems = this.cartItemsSubject.value;
    const existingItemIndex = currentItems.findIndex(item => item.car.id === car.id);

    let updatedItems: CartItem[];
    if (existingItemIndex !== -1) {
      // Update existing item quantity
      updatedItems = [...currentItems];
      updatedItems[existingItemIndex] = {
        ...updatedItems[existingItemIndex],
        quantity: updatedItems[existingItemIndex].quantity + quantity
      };
    } else {
      // Add new item
      updatedItems = [...currentItems, { car, quantity }];
    }

    this.cartItemsSubject.next(updatedItems);
    this.saveCartToStorage();
  }

  removeFromCart(carId: number): void {
    const currentItems = this.cartItemsSubject.value;
    const updatedItems = currentItems.filter(item => item.car.id !== carId);
    this.cartItemsSubject.next(updatedItems);
    this.saveCartToStorage();
  }

  updateQuantity(carId: number, newQuantity: number): void {
    if (newQuantity <= 0) {
      this.removeFromCart(carId);
      return;
    }

    const currentItems = this.cartItemsSubject.value;
    const updatedItems = currentItems.map(item =>
      item.car.id === carId ? { ...item, quantity: newQuantity } : item
    );

    this.cartItemsSubject.next(updatedItems);
    this.saveCartToStorage();
  }

  isInCart(carId: number): boolean {
    return this.cartItemsSubject.value.some(item => item.car.id === carId);
  }

  clearCart(): void {
    this.cartItemsSubject.next([]);
    localStorage.removeItem(this.CART_STORAGE_KEY);
  }

  private saveCartToStorage(): void {
    try {
      const cartItems = this.cartItemsSubject.value;
      localStorage.setItem(this.CART_STORAGE_KEY, JSON.stringify(cartItems));
    } catch (error) {
      console.error('Error saving cart to storage:', error);
    }
  }
}
