import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { Car } from '../models/car.model';

@Injectable({
  providedIn: 'root'
})
export class CarService {
  private apiUrl = 'http://localhost:5083/api/cars';

  private mockCars: Car[] = [
    {
      id: 1,
      brand: 'Tesla',
      model: 'Model 3',
      year: 2024,
      price: 46000,
      imageUrl: 'assets/cars/tesla-model-3.jpg',
      description: 'The Tesla Model 3 is an electric four-door sedan.'
    },
    {
      id: 2,
      brand: 'BMW',
      model: 'X5',
      year: 2024,
      price: 66000,
      imageUrl: 'assets/cars/BMW X5.jpeg',
      description: 'The BMW X5 is a luxury SUV with powerful performance.'
    },
    {
      id: 3,
      brand: 'Ford',
      model: 'Mustang Mach-E',
      year: 2024,
      price: 53000,
      imageUrl: 'assets/cars/ford-mustang-mach-e.jpg',
      description: 'The Ford Mustang Mach-E is an all-electric SUV.'
    },
    {
      id: 4,
      brand: 'Mercedes-Benz',
      model: 'C-Class',
      year: 2024,
      price: 55000,
      imageUrl: 'assets/cars/Mercedes-Benz C-Class.jpeg',
      description: 'The Mercedes-Benz C-Class is a luxury sedan.'
    },
    {
      id: 5,
      brand: 'Audi',
      model: 'A4',
      year: 2024,
      price: 48000,
      imageUrl: 'assets/cars/Audi A4.jpeg',
      description: 'The Audi A4 is a premium compact executive car.'
    },
    {
      id: 6,
      brand: 'Ferrari',
      model: '488',
      year: 2024,
      price: 330000,
      imageUrl: 'assets/cars/Ferrari 488.jpeg',
      description: 'The Ferrari 488 is a high-performance sports car.'
    },
    {
      id: 7,
      brand: 'Jaguar',
      model: 'F-Type',
      year: 2024,
      price: 74000,
      imageUrl: 'assets/cars/Jaguar F-Type.jpeg',
      description: 'The Jaguar F-Type is a luxury sports car.'
    },
    {
      id: 8,
      brand: 'Volkswagen',
      model: 'Polo',
      year: 2024,
      price: 22000,
      imageUrl: 'assets/cars/Volkswagen Polo.jpeg',
      description: 'The Volkswagen Polo is a reliable compact car.'
    },
    {
      id: 9,
      brand: 'Skoda',
      model: 'Octavia',
      year: 2024,
      price: 25000,
      imageUrl: 'assets/cars/Skoda Octavia.jpeg',
      description: 'The Skoda Octavia is a practical family car.'
    }
  ];

  constructor(private http: HttpClient) { }

  getCars(): Observable<Car[]> {
    return of(this.mockCars);
  }

  getCarById(id: number): Observable<Car | undefined> {
    const car = this.mockCars.find(c => c.id === id);
    return of(car);
  }

  searchCars(searchTerm: string): Observable<Car[]> {
    const filtered = this.mockCars.filter(car => 
      car.model.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.brand.toLowerCase().includes(searchTerm.toLowerCase())
    );
    return of(filtered);
  }

  getCarsByBrand(brand: string): Observable<Car[]> {
    const filtered = this.mockCars.filter(car => car.brand === brand);
    return of(filtered);
  }
}
