import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { CartService } from '../../services/cart.service';
import { OrderService } from '../../services/order.service';
import { AuthService } from '../../services/auth.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatSnackBarModule } from '@angular/material/snack-bar';
import { switchMap, take } from 'rxjs/operators';
import { forkJoin } from 'rxjs';

@Component({
  selector: 'app-payment',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, MatSnackBarModule],
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.scss']
})
export class PaymentComponent implements OnInit {
  paymentForm: FormGroup;
  cartTotal$ = this.cartService.getCartTotal();

  constructor(
    private fb: FormBuilder,
    private cartService: CartService,
    private orderService: OrderService,
    private authService: AuthService,
    private router: Router,
    private snackBar: MatSnackBar
  ) {
    this.paymentForm = this.fb.group({
      cardName: ['', [Validators.required]],
      cardNumber: ['', [Validators.required, Validators.pattern('^[0-9]{16}$')]],
      expiryDate: ['', [Validators.required, Validators.pattern('^(0[1-9]|1[0-2])\/?([0-9]{2})$')]],
      cvv: ['', [Validators.required, Validators.pattern('^[0-9]{3}$')]]
    });
  }

  ngOnInit(): void {}

  isFieldInvalid(fieldName: string): boolean {
    const field = this.paymentForm.get(fieldName);
    return field ? (field.invalid && (field.dirty || field.touched)) : false;
  }

  onSubmit(): void {
    if (this.paymentForm.valid) {
      // Get shipping address from session storage
      const shippingAddress = JSON.parse(sessionStorage.getItem('billingAddress') || '{}');
      
      // Get current cart items and user
      forkJoin({
        cartItems: this.cartService.cartItems$.pipe(take(1)),
        user: this.authService.user$.pipe(take(1)),
        total: this.cartService.getCartTotal().pipe(take(1))
      }).subscribe({
        next: ({ cartItems, user, total }) => {
          if (!user) {
            this.snackBar.open('Please login to place order', 'Close', {
              duration: 3000,
              panelClass: ['error-snackbar']
            });
            return;
          }

          const orderData = {
            userId: user.userId,
            items: cartItems.map(item => ({
              carId: item.carId,
              make: item.make,
              model: item.model,
              year: item.year,
              price: item.price,
              quantity: item.quantity
            })),
            totalAmount: total,
            shippingAddress: shippingAddress,
            orderDate: new Date(),
            status: 'Placed'
          };

          // Create order
          this.orderService.createOrder(orderData).pipe(
            switchMap(order => this.cartService.clearCart())
          ).subscribe({
            next: () => {
              // Show success message
              this.snackBar.open('Order placed successfully!', 'Close', {
                duration: 5000,
                horizontalPosition: 'center',
                verticalPosition: 'top',
                panelClass: ['success-snackbar']
              });

              // Clear shipping address from session storage
              sessionStorage.removeItem('billingAddress');

              // Navigate to orders page
              this.router.navigate(['/orders']);
            },
            error: (error) => {
              console.error('Error creating order:', error);
              this.snackBar.open('Failed to place order. Please try again.', 'Close', {
                duration: 5000,
                panelClass: ['error-snackbar']
              });
            }
          });
        },
        error: (error) => {
          console.error('Error getting cart data:', error);
          this.snackBar.open('Failed to process order. Please try again.', 'Close', {
            duration: 5000,
            panelClass: ['error-snackbar']
          });
        }
      });
    } else {
      Object.keys(this.paymentForm.controls).forEach(key => {
        const control = this.paymentForm.get(key);
        if (control?.invalid) {
          control.markAsTouched();
        }
      });
    }
  }

  goBack(): void {
    this.router.navigate(['/checkout']);
  }
}
