import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { CartService } from '../../services/cart.service';
import { AuthService } from '../../services/auth.service';
import { OrderService } from '../../services/order.service';
import { take, finalize } from 'rxjs/operators';
import { CartItem } from '../../models/cart-item.model';
import { forkJoin } from 'rxjs';

@Component({
  selector: 'app-payment',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.scss']
})
export class PaymentComponent implements OnInit {
  paymentForm: FormGroup;
  cartTotal$ = this.cartService.getCartTotal();
  cartItemCount$ = this.cartService.getCartItemCount();
  cartItems: CartItem[] = [];
  orderTotal: number = 0;
  isProcessing = false;
  errorMessage = '';
  orderPlaced = false;
  userId: number | null = null;

  constructor(
    private fb: FormBuilder,
    private cartService: CartService,
    private authService: AuthService,
    private orderService: OrderService,
    private router: Router
  ) {
    this.paymentForm = this.fb.group({
      cardType: ['credit', [Validators.required]],
      cardNumber: ['', [Validators.required, Validators.pattern('^[0-9]{16}$')]],
      cardholderName: ['', [Validators.required]],
      expiryMonth: ['', [Validators.required, Validators.min(1), Validators.max(12)]],
      expiryYear: ['', [Validators.required, Validators.pattern('^[0-9]{4}$')]],
      cvv: ['', [Validators.required, Validators.pattern('^[0-9]{3,4}$')]],
      saveCard: [false]
    });
  }

  ngOnInit(): void {
    // Get user ID and pre-fill cardholder name
    this.authService.user$.pipe(take(1)).subscribe(user => {
      if (user) {
        this.userId = user.userId;
        this.paymentForm.patchValue({
          cardholderName: user.name || user.username
        });
      }
    });

    // Get cart items and total
    this.cartService.cartItems$.pipe(take(1)).subscribe(items => {
      this.cartItems = items;
    });
    this.cartService.getCartTotal().pipe(take(1)).subscribe(total => {
      this.orderTotal = total;
    });

    // Load billing address
    const billingAddress = localStorage.getItem('billingAddress');
    if (billingAddress) {
      const address = JSON.parse(billingAddress);
      if (address.fullName) {
        this.paymentForm.patchValue({
          cardholderName: address.fullName
        });
      }
    }
  }

  isFieldInvalid(fieldName: string): boolean {
    const field = this.paymentForm.get(fieldName);
    return field ? (field.invalid && (field.dirty || field.touched)) : false;
  }

  onSubmit(): void {
    if (this.paymentForm.valid) {
      this.isProcessing = true;
      this.errorMessage = '';
      
      // Simulate payment processing
      setTimeout(() => {
        this.processOrder();
      }, 1500);
    } else {
      Object.keys(this.paymentForm.controls).forEach(key => {
        const control = this.paymentForm.get(key);
        if (control?.invalid) {
          control.markAsTouched();
        }
      });
    }
  }

  processOrder(): void {
    if (!this.userId) {
      this.errorMessage = 'User not authenticated';
      this.isProcessing = false;
      return;
    }

    // Get billing address
    const billingAddress = localStorage.getItem('billingAddress');
    const shippingAddress = billingAddress ? JSON.parse(billingAddress) : null;

    if (!shippingAddress) {
      this.errorMessage = 'Shipping address not found';
      this.isProcessing = false;
      return;
    }

    // Create an order for each item in the cart
    const orderRequests = this.cartItems.map(item => {
      const orderData = {
        userId: this.userId!,
        carId: item.id,
        quantity: item.quantity,
        totalPrice: item.price * item.quantity,
        shippingAddress: JSON.stringify(shippingAddress)
      };

      return this.orderService.createOrder(orderData);
    });

    // Process all orders
    forkJoin(orderRequests)
      .pipe(
        finalize(() => this.isProcessing = false)
      )
      .subscribe({
        next: (orders) => {
          this.orderPlaced = true;
          // Clear cart after successful order creation
          this.cartService.clearCart().subscribe();
          // Remove billing address from localStorage
          localStorage.removeItem('billingAddress');
        },
        error: (error) => {
          this.errorMessage = error.message || 'Failed to create order. Please try again.';
        }
      });
  }

  goBack(): void {
    this.router.navigate(['/checkout']);
  }

  continueShopping(): void {
    this.router.navigate(['/cars']);
  }
}
