import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { Observable } from 'rxjs';
import { CartService, CartItem } from '../../services/cart.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-cart-view',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule,
    MatIconModule,
    MatButtonModule
  ],
  templateUrl: './cart-view.component.html',
  styles: [`
    .cart-container {
      max-width: 1200px;
      margin: 2rem auto;
      padding: 1rem;
    }

    .cart-items {
      display: flex;
      flex-direction: column;
      gap: 1rem;
    }

    .cart-item {
      display: flex;
      gap: 1rem;
      padding: 1rem;
      border: 1px solid #ddd;
      border-radius: 8px;
      background-color: white;
    }

    .cart-item img {
      width: 150px;
      height: 100px;
      object-fit: cover;
      border-radius: 4px;
    }

    .item-details {
      flex: 1;
    }

    .quantity-controls {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      margin: 1rem 0;
    }

    .cart-summary {
      margin-top: 2rem;
      padding: 1rem;
      border-top: 2px solid #ddd;
    }

    .cart-actions {
      display: flex;
      gap: 1rem;
      justify-content: flex-end;
      margin-top: 1rem;
    }

    .empty-cart {
      text-align: center;
      padding: 2rem;
      color: #666;
    }

    .empty-cart-actions {
      margin-top: 1rem;
    }
  `]
})
export class CartViewComponent implements OnInit {
  cartItems$: Observable<CartItem[]>;

  constructor(private cartService: CartService, private router: Router) {
    this.cartItems$ = this.cartService.cartItems$;
  }

  ngOnInit(): void {
    // Cart items are automatically loaded by the service
  }

  updateQuantity(itemId: number, quantity: number): void {
    if (quantity > 0) {
      this.cartService.updateQuantity(itemId, quantity).subscribe({
        error: (error) => console.error('Error updating quantity:', error)
      });
    }
  }

  removeFromCart(itemId: number): void {
    this.cartService.removeFromCart(itemId).subscribe({
      error: (error) => console.error('Error removing item:', error)
    });
  }

  clearCart(): void {
    this.cartService.clearCart().subscribe({
      error: (error) => console.error('Error clearing cart:', error)
    });
  }

  getCartTotal(): number {
    return this.cartService.getCartTotal();
  }

  checkout(): void {
    // Implement checkout logic
    console.log('Checkout not implemented yet');
  }

  continueShopping(): void {
    this.router.navigate(['/cars']);
  }

  calculateItemTotal(item: CartItem): number {
    return item.car.price * item.quantity;
  }
}
