import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { CommonModule, DecimalPipe } from '@angular/common';
import { Car } from '../../models/car.model';
import { CarService } from '../../services/car.service';
import { CartService } from '../../services/cart.service';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-car-detail',
  standalone: true,
  imports: [CommonModule, DecimalPipe],
  template: `
    <div class="car-detail" *ngIf="car">
      <h2>{{car.brand}} {{car.model}}</h2>
      <img [src]="car.imageUrl" (error)="onImageError($event)" [alt]="car.brand + ' ' + car.model">
      <div class="car-info">
        <p class="description">{{car.description}}</p>
        <p class="price">{{car.price | number:'1.2-2'}} USD</p>
        <div class="quantity-controls" *ngIf="authService.isLoggedIn()">
          <button class="quantity-btn" (click)="decreaseQuantity()">
            <i class="fas fa-minus"></i>
          </button>
          <span class="quantity-display">{{quantity}}</span>
          <button class="quantity-btn" (click)="increaseQuantity()">
            <i class="fas fa-plus"></i>
          </button>
        </div>
        <button class="add-to-cart" 
                (click)="addToCart()" 
                [disabled]="isAddingToCart || !authService.isLoggedIn() || isInCart"
                *ngIf="authService.isLoggedIn(); else loginPrompt">
          <i class="fas fa-shopping-cart"></i>
          {{buttonText}}
        </button>
        <ng-template #loginPrompt>
          <button class="login-prompt" (click)="navigateToLogin()">
            Login to Add to Cart
          </button>
        </ng-template>
      </div>
    </div>
  `,
  styles: [`
    .car-detail {
      padding: 2rem;
      max-width: 1200px;
      margin: 0 auto;
    }

    img {
      max-width: 100%;
      height: auto;
      border-radius: 8px;
      margin: 1rem 0;
    }

    .car-info {
      margin-top: 2rem;
    }

    .description {
      color: #666;
      margin: 1rem 0;
    }

    .price {
      font-size: 1.5rem;
      font-weight: bold;
      color: #007bff;
      margin: 1rem 0;
    }

    .quantity-controls {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin: 1rem 0;
    }

    .quantity-btn {
      padding: 0.5rem 1rem;
      border: 1px solid #ddd;
      background: white;
      cursor: pointer;
      border-radius: 4px;
      color: #007bff;
      transition: all 0.2s ease;

      &:hover {
        background: #007bff;
        color: white;
        border-color: #007bff;
      }

      i {
        font-size: 0.8rem;
      }
    }

    .quantity-display {
      min-width: 40px;
      text-align: center;
      font-size: 1.1rem;
      font-weight: bold;
    }

    .add-to-cart {
      padding: 0.75rem 1.5rem;
      background: #007bff;
      color: white;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 1rem;
      width: 100%;
      max-width: 300px;
      margin-top: 1rem;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 0.5rem;
      transition: all 0.2s ease;

      i {
        font-size: 1.1rem;
      }

      &:hover:not(:disabled) {
        background: #0056b3;
      }

      &:disabled {
        background: #ccc;
        cursor: not-allowed;
      }

      &.added {
        background: #28a745;
      }
    }

    .login-prompt {
      background: #6c757d;
      padding: 0.75rem 1.5rem;
      color: white;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 1rem;
      width: 100%;
      max-width: 300px;
      margin-top: 1rem;

      &:hover {
        background: #5a6268;
      }
    }
  `]
})
export class CarDetailComponent implements OnInit {
  car: Car | undefined;
  quantity: number = 1;
  isAddingToCart: boolean = false;
  isInCart: boolean = false;

  get buttonText(): string {
    if (this.isInCart) {
      return 'Added to Cart';
    }
    if (this.isAddingToCart) {
      return 'Adding...';
    }
    return 'Add to Cart';
  }

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private carService: CarService,
    private cartService: CartService,
    public authService: AuthService
  ) {}

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.loadCarDetails(id);
    this.checkIfInCart(id);
  }

  loadCarDetails(id: number): void {
    this.carService.getCarById(id).subscribe({
      next: (car) => {
        this.car = car;
      },
      error: (error) => {
        console.error('Error loading car details:', error);
      }
    });
  }

  checkIfInCart(carId: number): void {
    this.cartService.cartItems$.subscribe(items => {
      this.isInCart = items.some(item => item.car.id === carId);
    });
  }

  increaseQuantity(): void {
    this.quantity++;
  }

  decreaseQuantity(): void {
    if (this.quantity > 1) {
      this.quantity--;
    }
  }

  addToCart(): void {
    if (!this.car || this.isAddingToCart || !this.authService.isLoggedIn() || this.isInCart) return;

    this.isAddingToCart = true;
    this.cartService.addToCart(this.car, this.quantity);
    this.isInCart = true;
    this.isAddingToCart = false;
  }

  navigateToLogin(): void {
    this.router.navigate(['/login']);
  }

  onImageError(event: Event): void {
    const img = event.target as HTMLImageElement;
    img.src = 'assets/cars/placeholder.jpg';
  }
}
