import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { CartService } from '../../services/cart.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <header class="header">
      <div class="logo">
        <a routerLink="/">Car Ecommerce</a>
      </div>
      <nav class="nav-links">
        <a routerLink="/cars">Cars</a>
      </nav>
      <div class="auth-buttons">
        <ng-container *ngIf="!(authService.user$ | async); else loggedIn">
          <button class="login-btn" routerLink="/login">Login</button>
          <button class="register-btn" routerLink="/register">Register</button>
        </ng-container>
        <ng-template #loggedIn>
          <span class="welcome-text">Welcome, {{(authService.user$ | async)?.username}}!</span>
          <button class="cart-btn" routerLink="/cart">
            <i class="fas fa-shopping-cart"></i>
            <span class="cart-text">Cart</span>
            <span class="cart-count" *ngIf="cartService.cartCount$ | async as count">
              {{ count }}
            </span>
          </button>
          <button class="logout-btn" (click)="logout()">Logout</button>
        </ng-template>
      </div>
    </header>
  `,
  styles: [`
    .header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 1rem 2rem;
      background-color: #333;
      color: white;
    }

    .logo a {
      color: white;
      text-decoration: none;
      font-size: 1.5rem;
      font-weight: bold;
    }

    .nav-links {
      display: flex;
      gap: 1.5rem;
      
      a {
        color: white;
        text-decoration: none;
        font-size: 1.1rem;
        
        &:hover {
          color: #007bff;
        }
      }
    }

    .auth-buttons {
      display: flex;
      gap: 1rem;
      align-items: center;

      button {
        padding: 0.5rem 1rem;
        border-radius: 4px;
        font-size: 1rem;
        cursor: pointer;
        transition: all 0.2s ease;
        border: none;
      }

      .login-btn {
        background-color: transparent;
        color: white;
        border: 1px solid white;

        &:hover {
          background-color: white;
          color: #333;
        }
      }

      .register-btn {
        background-color: #007bff;
        color: white;

        &:hover {
          background-color: #0056b3;
        }
      }

      .cart-btn {
        background-color: #007bff;
        color: white;
        position: relative;
        padding: 0.5rem 1rem;
        font-size: 1rem;
        border: none;
        border-radius: 4px;
        display: flex;
        align-items: center;
        gap: 0.5rem;
        transition: all 0.2s ease;

        i {
          font-size: 1.2rem;
          color: white;
        }

        .cart-text {
          color: white;
          font-weight: 500;
        }

        .cart-count {
          position: absolute;
          top: -8px;
          right: -8px;
          background-color: #dc3545;
          color: white;
          border-radius: 50%;
          padding: 0.2rem 0.5rem;
          font-size: 0.8rem;
          min-width: 20px;
          text-align: center;
          font-weight: bold;
          border: 2px solid white;
        }

        &:hover {
          background-color: #0056b3;
        }
      }

      .logout-btn {
        background-color: #dc3545;
        color: white;
        border: none;

        &:hover {
          background-color: #c82333;
        }
      }

      .welcome-text {
        color: white;
        font-size: 1.1rem;
        margin-right: 1rem;
      }
    }
  `]
})
export class HeaderComponent {
  constructor(
    public authService: AuthService,
    public cartService: CartService,
    private router: Router
  ) {}

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
