import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, tap, catchError } from 'rxjs';
import { environment } from '../../environments/environment';
import { CartItem } from '../models/cart-item.model';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private apiUrl = `${environment.apiUrl}/api/cart`;
  private cartItemsSubject = new BehaviorSubject<CartItem[]>([]);
  cartItems$ = this.cartItemsSubject.asObservable();
  
  private cartCountSubject = new BehaviorSubject<number>(0);
  cartCount$ = this.cartCountSubject.asObservable();

  private totalPrice = new BehaviorSubject<number>(0);

  constructor(private http: HttpClient) {
    this.loadCart();
  }

  loadCart(): void {
    this.http.get<CartItem[]>(this.apiUrl).subscribe({
      next: (items) => {
        this.cartItemsSubject.next(items);
        this.updateCartCount();
        this.calculateTotal();
      },
      error: (error) => {
        console.error('Error loading cart:', error);
        // Initialize with empty cart if API fails
        this.cartItemsSubject.next([]);
        this.updateCartCount();
        this.calculateTotal();
      }
    });
  }

  addToCart(carId: number, quantity: number = 1): Observable<any> {
    return this.http.post(this.apiUrl, { carId, quantity }).pipe(
      tap(() => this.loadCart()),
      catchError((error) => {
        console.error('Error adding to cart:', error);
        throw error;
      })
    );
  }

  updateQuantity(cartItemId: number, quantity: number): Observable<any> {
    return this.http.put(`${this.apiUrl}/${cartItemId}`, { quantity }).pipe(
      tap(() => this.loadCart()),
      catchError((error) => {
        console.error('Error updating quantity:', error);
        throw error;
      })
    );
  }

  removeFromCart(cartItemId: number): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${cartItemId}`).pipe(
      tap(() => this.loadCart()),
      catchError((error) => {
        console.error('Error removing item:', error);
        throw error;
      })
    );
  }

  clearCart(): Observable<any> {
    return this.http.delete(`${this.apiUrl}/clear`).pipe(
      tap(() => this.loadCart()),
      catchError((error) => {
        console.error('Error clearing cart:', error);
        throw error;
      })
    );
  }

  private updateCartCount(): void {
    const totalItems = this.cartItemsSubject.value.reduce(
      (sum, item) => sum + item.quantity, 0
    );
    this.cartCountSubject.next(totalItems);
  }

  getCartItems(): Observable<CartItem[]> {
    return this.cartItemsSubject.asObservable();
  }

  getTotalPrice(): Observable<number> {
    return this.totalPrice.asObservable();
  }

  private calculateTotal(): void {
    const total = this.cartItemsSubject.value.reduce((sum, item) => sum + (item.car.price * item.quantity), 0);
    this.totalPrice.next(total);
  }
}
