import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import { tap } from 'rxjs/operators';
import { Car } from '../models/car.model';

export interface CartItem {
  id: number;
  car: Car;
  quantity: number;
  totalPrice: number;
}

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private apiUrl = 'http://localhost:5083/api/cart';
  private cartItemsSubject = new BehaviorSubject<CartItem[]>([]);
  cartItems$ = this.cartItemsSubject.asObservable();

  constructor(private http: HttpClient) {
    this.loadCart();
  }

  private loadCart(): void {
    this.http.get<CartItem[]>(this.apiUrl).subscribe({
      next: (items) => this.cartItemsSubject.next(items),
      error: (error) => console.error('Error loading cart:', error)
    });
  }

  addToCart(carId: number): Observable<CartItem> {
    return this.http.post<CartItem>(`${this.apiUrl}`, { carId, quantity: 1 }).pipe(
      tap(() => this.loadCart())
    );
  }

  removeFromCart(itemId: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${itemId}`).pipe(
      tap(() => this.loadCart())
    );
  }

  updateQuantity(itemId: number, quantity: number): Observable<CartItem> {
    return this.http.put<CartItem>(`${this.apiUrl}/${itemId}`, { quantity }).pipe(
      tap(() => this.loadCart())
    );
  }

  clearCart(): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}`).pipe(
      tap(() => this.cartItemsSubject.next([]))
    );
  }

  getCartTotal(): number {
    return this.cartItemsSubject.value.reduce((total, item) => total + item.totalPrice, 0);
  }

  getCartItemCount(): number {
    return this.cartItemsSubject.value.reduce((count, item) => count + item.quantity, 0);
  }
}
