import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CartService } from '../../services/cart.service';
import { AuthService } from '../../services/auth.service';
import { Order, OrderItem } from '../../models/order.model';
import { CartItem } from '../../models/cart-item.model';
import { take } from 'rxjs/operators';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-payment',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.scss']
})
export class PaymentComponent implements OnInit {
  paymentForm: FormGroup;
  isLoading = false;
  orderPlaced = false;
  errorMessage: string | null = null;
  isProcessing = false;
  cartItems$ = this.cartService.cartItems$;
  cartTotal$ = this.cartService.getCartTotal();
  cartItemCount$ = this.cartService.cartItems$.pipe(
    map(items => items.length)
  );
  orderTotal = 0;
  cartItems: CartItem[] = [];

  constructor(
    private fb: FormBuilder,
    private cartService: CartService,
    private authService: AuthService,
    private router: Router
  ) {
    this.paymentForm = this.fb.group({
      cardType: ['credit', Validators.required],
      cardNumber: ['', [Validators.required, Validators.pattern(/^\d{16}$/)]],
      cardholderName: ['', Validators.required],
      expiryMonth: ['', [Validators.required, Validators.min(1), Validators.max(12)]],
      expiryYear: ['', [Validators.required, Validators.pattern(/^\d{4}$/)]],
      cvv: ['', [Validators.required, Validators.pattern(/^\d{3,4}$/)]],
      saveCard: [false]
    });
  }

  ngOnInit(): void {
    this.authService.user$.pipe(take(1)).subscribe(user => {
      if (user?.name || user?.username) {
        this.paymentForm.patchValue({
          cardholderName: user.name || user.username
        });
      }
    });

    // Calculate order total
    this.cartTotal$.pipe(take(1)).subscribe(total => {
      this.orderTotal = total;
    });

    this.cartItems$.pipe(take(1)).subscribe(items => {
      this.cartItems = items;
    });
  }

  isFieldInvalid(fieldName: string): boolean {
    const field = this.paymentForm.get(fieldName);
    return field ? field.invalid && (field.dirty || field.touched) : false;
  }

  onSubmit(): void {
    if (this.paymentForm.valid && !this.isProcessing) {
      this.isProcessing = true;
      this.errorMessage = null;
      
      // Simulate payment processing
      setTimeout(() => {
        this.processOrder();
      }, 2000);
    }
  }

  private processOrder(): void {
    if (!this.cartItems.length) {
      this.errorMessage = 'Your cart is empty';
      this.isProcessing = false;
      return;
    }

    this.authService.user$.pipe(take(1)).subscribe({
      next: (user) => {
        if (!user) {
          this.errorMessage = 'User not authenticated';
          this.isProcessing = false;
          return;
        }

        const order: Order = {
          id: `ORD-${Date.now()}`,
          userId: user.userId.toString(),
          date: new Date().toISOString(),
          items: this.cartItems.map(item => ({
            id: item.carId.toString(),
            name: `${item.make} ${item.model} ${item.year}`,
            quantity: item.quantity,
            price: item.price,
            imageUrl: undefined
          })),
          total: this.orderTotal,
          status: 'pending',
          shippingAddress: JSON.parse(localStorage.getItem('checkoutAddress') || '{}'),
          paymentDetails: {
            cardType: this.paymentForm.get('cardType')?.value,
            lastFourDigits: this.paymentForm.get('cardNumber')?.value.slice(-4)
          }
        };

        // Here you would typically send the order to your backend
        console.log('Processing order:', order);
        
        // Clear cart and update state
        this.cartService.clearCart();
        this.isProcessing = false;
        this.orderPlaced = true;
      },
      error: (error) => {
        console.error('Error getting user:', error);
        this.errorMessage = 'Failed to process order. Please try again.';
        this.isProcessing = false;
      }
    });
  }

  goBack(): void {
    this.router.navigate(['/checkout']);
  }

  continueShopping(): void {
    this.router.navigate(['/']);
  }

  viewOrders(): void {
    this.router.navigate(['/orders']);
  }
}
