import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { tap } from 'rxjs/operators';

export interface CartItem {
  id: number;
  carId: number;
  carMake: string;
  carModel: string;
  carYear: number;
  carPrice: number;
  quantity: number;
  totalPrice: number;
  createdAt: Date;
  updatedAt?: Date;
}

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private apiUrl = 'http://localhost:5083/api/cart';
  private cartItemsSubject = new BehaviorSubject<CartItem[]>([]);
  cartItems$ = this.cartItemsSubject.asObservable();

  constructor(private http: HttpClient) {
    this.loadCart();
  }

  private loadCart(): void {
    this.getCartItems().subscribe({
      next: (items) => this.cartItemsSubject.next(items),
      error: (error) => console.error('Error loading cart:', error)
    });
  }

  getCartItems(): Observable<CartItem[]> {
    return this.http.get<CartItem[]>(this.apiUrl);
  }

  addToCart(carId: number, quantity: number = 1): Observable<any> {
    return this.http.post(this.apiUrl, { carId, quantity }).pipe(
      tap(() => this.loadCart())
    );
  }

  updateCartItem(id: number, quantity: number): Observable<any> {
    return this.http.put(`${this.apiUrl}/${id}`, { quantity }).pipe(
      tap(() => this.loadCart())
    );
  }

  removeFromCart(id: number): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${id}`).pipe(
      tap(() => this.loadCart())
    );
  }

  clearCart(): Observable<any> {
    return this.http.delete(`${this.apiUrl}/clear`).pipe(
      tap(() => this.cartItemsSubject.next([]))
    );
  }
}
