import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Car } from '../models/car.model';

@Injectable({
  providedIn: 'root'
})
export class CarService {
  private apiUrl = 'http://localhost:5083/api/cars';

  constructor(private http: HttpClient) { }

  getCars(): Observable<Car[]> {
    return this.http.get<Car[]>(this.apiUrl);
  }

  getCarById(id: number): Observable<Car> {
    return this.http.get<Car>(`${this.apiUrl}/${id}`);
  }

  searchCars(searchTerm: string): Observable<Car[]> {
    return this.http.get<Car[]>(`${this.apiUrl}/search?term=${searchTerm}`);
  }

  getCarsByBrand(brand: string): Observable<Car[]> {
    return this.http.get<Car[]>(`${this.apiUrl}/brand/${brand}`);
  }
}
