import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Car } from '../../models/car.model';
import { CarService } from '../../services/car.service';
import { CartService } from '../../services/cart.service';
import { Subject, takeUntil, finalize } from 'rxjs';
import { MatButtonModule } from '@angular/material/button';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';

@Component({
  selector: 'app-car-detail',
  standalone: true,
  imports: [
    CommonModule,
    MatButtonModule,
    MatProgressSpinnerModule
  ],
  templateUrl: './car-detail.component.html',
  styleUrls: ['./car-detail.component.scss']
})
export class CarDetailComponent implements OnInit, OnDestroy {
  car: Car | undefined;
  addedToCart = false;
  isLoading = false;
  private destroy$ = new Subject<void>();
  private notificationTimeout: any;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private carService: CarService,
    private cartService: CartService,
    private snackBar: MatSnackBar
  ) {}

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.loadCarDetails(id);
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
    if (this.notificationTimeout) {
      clearTimeout(this.notificationTimeout);
    }
  }

  loadCarDetails(id: number): void {
    this.isLoading = true;
    this.carService.getCarById(id).pipe(
      takeUntil(this.destroy$),
      finalize(() => this.isLoading = false)
    ).subscribe({
      next: (car) => {
        this.car = car;
      },
      error: (error) => {
        console.error('Error loading car details:', error);
        this.showError('Error loading car details. Please try again.');
      }
    });
  }

  addToCart(): void {
    if (!this.car) return;

    this.isLoading = true;
    this.cartService.addToCart(this.car).pipe(
      takeUntil(this.destroy$),
      finalize(() => this.isLoading = false)
    ).subscribe({
      next: () => {
        this.addedToCart = true;
        this.showSuccess('Item added to cart successfully!');
        if (this.notificationTimeout) {
          clearTimeout(this.notificationTimeout);
        }
        this.notificationTimeout = setTimeout(() => {
          this.addedToCart = false;
        }, 3000);
      },
      error: (error) => {
        console.error('Error adding to cart:', error);
        this.showError('Error adding item to cart. Please try again.');
      }
    });
  }

  viewCart(): void {
    this.router.navigate(['/cart']);
  }

  onImageError(event: Event): void {
    const img = event.target as HTMLImageElement;
    img.src = 'assets/cars/placeholder.txt';
  }

  private showError(message: string): void {
    this.snackBar.open(message, 'Close', {
      duration: 5000,
      panelClass: ['error-snackbar']
    });
  }

  private showSuccess(message: string): void {
    this.snackBar.open(message, 'Close', {
      duration: 3000,
      panelClass: ['success-snackbar']
    });
  }
}
