import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CarService } from '../../services/car.service';
import { Car } from '../../models/car.model';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-car-list',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './car-list.component.html',
  styleUrls: ['./car-list.component.scss']
})
export class CarListComponent implements OnInit {
  cars: Car[] = [];
  filteredCars: Car[] = [];
  brands: string[] = [];
  searchTerm: string = '';
  selectedBrand: string = '';

  constructor(private router: Router, private carService: CarService) {}

  ngOnInit(): void {
    this.loadCars();
  }

  loadCars(): void {
    this.carService.getCars().subscribe({
      next: (cars) => {
        this.cars = cars;
        this.filteredCars = cars;
        this.brands = [...new Set(cars.map(car => car.make))];
      },
      error: (error) => {
        console.error('Error loading cars:', error);
      }
    });
  }

  filterCars(): void {
    this.filteredCars = this.cars.filter(car => {
      const matchesSearch = car.model.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
                          car.make.toLowerCase().includes(this.searchTerm.toLowerCase());
      const matchesBrand = !this.selectedBrand || car.make === this.selectedBrand;
      return matchesSearch && matchesBrand;
    });
  }

  onSearchChange(): void {
    this.filterCars();
  }

  onBrandChange(): void {
    this.filterCars();
  }

  viewCarDetails(id: number): void {
    this.router.navigate(['/cars', id]);
  }

  onImageError(event: Event): void {
    const img = event.target as HTMLImageElement;
    img.src = 'assets/cars/placeholder.txt';
  }
}
