import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { Observable, map } from 'rxjs';
import { CartService } from '../../services/cart.service';
import { CartItem } from '../../models/cart-item.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-cart-view',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule,
    MatIconModule,
    MatButtonModule
  ],
  templateUrl: './cart-view.component.html',
  styleUrls: ['./cart-view.component.scss']
})
export class CartViewComponent implements OnInit {
  cartItems$: Observable<CartItem[]>;
  totalPrice$: Observable<number>;

  constructor(private cartService: CartService, private router: Router) {
    this.cartItems$ = this.cartService.getCartItems();
    this.totalPrice$ = this.cartService.getTotalPrice().pipe(
      map(price => price || 0) // Ensure we never get null
    );
  }

  ngOnInit(): void {
    this.cartService.loadCart();
  }

  updateQuantity(item: CartItem, change: number): void {
    const newQuantity = item.quantity + change;
    if (newQuantity > 0) {
      this.cartService.updateQuantity(item.id, newQuantity).subscribe({
        error: (error) => console.error('Error updating quantity:', error)
      });
    }
  }

  removeItem(item: CartItem): void {
    this.cartService.removeFromCart(item.id).subscribe({
      error: (error) => console.error('Error removing item:', error)
    });
  }

  clearCart(): void {
    this.cartService.clearCart().subscribe({
      error: (error) => console.error('Error clearing cart:', error)
    });
  }

  checkout(): void {
    // TODO: Implement checkout logic
    this.router.navigate(['/checkout']);
  }

  continueShopping(): void {
    this.router.navigate(['/cars']);
  }
}
