import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, map } from 'rxjs';
import { Car } from '../models/car.model';
import { environment } from '../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class CarService {
  private apiUrl = `${environment.apiUrl}/api/cars`;

  constructor(private http: HttpClient) { }

  private getHttpOptions(): { headers: HttpHeaders } {
    return {
      headers: new HttpHeaders({
        'Content-Type': 'application/json'
      })
    };
  }

  private getImageUrl(car: Car): string {
    // Map the car make and model to the corresponding image filename
    const imageMap: { [key: string]: string } = {
      'Toyota Camry': 'Camry.jpeg',
      'Honda CR-V': 'CR-V.jpg',
      'Tesla Model 3': 'model-3.jpg',
      'BMW X5': 'X5.jpeg',
      'Ford F-150': 'F-150.jpeg',
      'Ford Mustang Mach-E': 'ford-mustang-mach-e.jpg',
      'Audi A4': 'A4.jpeg',
      'Mercedes-Benz C-Class': 'C-Class.jpeg',
      'Ferrari 488': '488.jpeg',
      'Jaguar F-Type': 'Jaguar F-Type.jpeg',
      'Volkswagen Polo': 'Volkswagen Polo.jpeg',
      'Skoda Octavia': 'Skoda Octavia.jpeg'
    };

    const key = `${car.brand} ${car.model}`;
    const filename = imageMap[key] || 'placeholder.jpg';
    return `assets/cars/${filename}`;
  }

  private transformCarData(car: Car): Car {
    return {
      ...car,
      imageUrl: this.getImageUrl(car)
    };
  }

  getCars(): Observable<Car[]> {
    return this.http.get<Car[]>(this.apiUrl, this.getHttpOptions()).pipe(
      map(cars => cars.map(car => this.transformCarData(car)))
    );
  }

  getCarById(id: number): Observable<Car> {
    return this.http.get<Car>(`${this.apiUrl}/${id}`, this.getHttpOptions()).pipe(
      map(car => this.transformCarData(car))
    );
  }

  searchCars(searchTerm: string): Observable<Car[]> {
    return this.http.get<Car[]>(`${this.apiUrl}/search?term=${searchTerm}`, this.getHttpOptions()).pipe(
      map(cars => cars.map(car => this.transformCarData(car)))
    );
  }

  getCarsByBrand(brand: string): Observable<Car[]> {
    return this.http.get<Car[]>(`${this.apiUrl}/brand/${brand}`, this.getHttpOptions()).pipe(
      map(cars => cars.map(car => this.transformCarData(car)))
    );
  }
}
