import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { RouterModule } from '@angular/router';
import { OrderService, Order } from '../../services/order.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-orders',
  standalone: true,
  imports: [
    CommonModule,
    MatCardModule,
    MatButtonModule,
    MatProgressSpinnerModule,
    RouterModule
  ],
  template: `
    <div class="orders-container">
      <h2>My Orders</h2>
      
      <div *ngIf="isLoading" class="loading">
        <mat-spinner diameter="40"></mat-spinner>
      </div>

      <div *ngIf="error" class="error-message">
        {{ error }}
      </div>

      <div *ngIf="!isLoading && !error">
        <div *ngIf="orders.length === 0" class="no-orders">
          <p>You haven't placed any orders yet.</p>
          <button mat-raised-button color="primary" routerLink="/cars">Start Shopping</button>
        </div>

        <mat-card *ngFor="let order of orders" class="order-card">
          <mat-card-header>
            <mat-card-title>Order #{{ order.id }}</mat-card-title>
            <mat-card-subtitle>
              {{ order.orderDate | date:'medium' }}
              <span class="status" [ngClass]="order.status.toLowerCase()">
                {{ order.status }}
              </span>
            </mat-card-subtitle>
          </mat-card-header>

          <mat-card-content>
            <div class="items-list">
              <div *ngFor="let item of order.items" class="order-item">
                <span>{{ item.make }} {{ item.model }} ({{ item.year }})</span>
                <span>{{ item.quantity }} x {{ item.price | currency }}</span>
              </div>
            </div>

            <div class="shipping-info">
              <h4>Shipping Address</h4>
              <p>{{ order.shippingAddress.fullName }}</p>
              <p>{{ order.shippingAddress.addressLine1 }}</p>
              <p *ngIf="order.shippingAddress.addressLine2">{{ order.shippingAddress.addressLine2 }}</p>
              <p>{{ order.shippingAddress.city }}, {{ order.shippingAddress.state }} {{ order.shippingAddress.zipCode }}</p>
              <p>Phone: {{ order.shippingAddress.phone }}</p>
            </div>

            <div class="total-amount">
              <strong>Total Amount: {{ order.totalAmount | currency }}</strong>
            </div>
          </mat-card-content>

          <mat-card-actions *ngIf="order.status === 'Placed'">
            <button mat-button color="warn" (click)="cancelOrder(order.id)">
              Cancel Order
            </button>
          </mat-card-actions>
        </mat-card>
      </div>
    </div>
  `,
  styles: [`
    .orders-container {
      padding: 20px;
      max-width: 1200px;
      margin: 0 auto;
    }
    
    .loading {
      display: flex;
      justify-content: center;
      padding: 20px;
    }
    
    .error-message {
      color: red;
      text-align: center;
      padding: 20px;
    }
    
    .no-orders {
      text-align: center;
      padding: 20px;
    }
    
    .order-card {
      margin-bottom: 20px;
    }
    
    .order-item {
      display: flex;
      justify-content: space-between;
      margin-bottom: 10px;
    }
    
    .shipping-info {
      margin: 20px 0;
      padding: 15px;
      background-color: #f5f5f5;
      border-radius: 4px;
    }
    
    .status {
      padding: 4px 8px;
      border-radius: 4px;
      margin-left: 10px;
      font-size: 0.9em;
    }
    
    .placed { background-color: #e3f2fd; }
    .processing { background-color: #fff3e0; }
    .shipped { background-color: #e8f5e9; }
    .delivered { background-color: #e0f2f1; }
    .cancelled { background-color: #ffebee; }
  `]
})
export class OrdersComponent implements OnInit {
  orders: Order[] = [];
  isLoading = false;
  error: string | null = null;

  constructor(
    private orderService: OrderService,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    this.loadOrders();
  }

  loadOrders(): void {
    this.isLoading = true;
    this.error = null;

    this.authService.user$.subscribe(user => {
      if (!user) {
        this.error = 'Please login to view orders';
        this.isLoading = false;
        return;
      }

      this.orderService.getUserOrders(user.userId).subscribe({
        next: (orders) => {
          this.orders = orders;
          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error loading orders:', error);
          this.error = 'Failed to load orders. Please try again.';
          this.isLoading = false;
        }
      });
    });
  }

  cancelOrder(orderId: string): void {
    this.orderService.cancelOrder(orderId).subscribe({
      next: (updatedOrder) => {
        this.orders = this.orders.map(order => 
          order.id === updatedOrder.id ? updatedOrder : order
        );
      },
      error: (error) => {
        console.error('Error cancelling order:', error);
        this.error = 'Failed to cancel order. Please try again.';
      }
    });
  }
}
